"use strict";var images=document.querySelectorAll("img"),isVisible=function(e){var i=e.getBoundingClientRect(),t=document.documentElement;return i.top>=0&&i.left>=0&&i.bottom<=(window.innerHeight||t.clientHeight)&&i.right<=(window.innerWidth||t.clientWidth)},handleScroll=function(e){images.forEach(function(e){if(isVisible(e)){var i=e.dataset.src;e.setAttribute("src",i),e.classList.remove("hidden-image"),e.classList.add("visible-image")}})};window.addEventListener("scroll",handleScroll);